script:
var timespan_obj = {"daily":{}, "monthly":{}};
frappe.call({
		method: "automotive.events.vehicle_movement.get_vehicle_movement",
		args: {
		    customer_list:["C00883", "C00276", "C01053"]
		},
		freeze: 1,
		freeze_message: __("Fetching.."),
		callback: (res) => {
			if(res.message) {
			    console.log(res.message);
			    let tbody_ele = root_element.querySelector('#vehicle_movement_tbody');
                // Clear any existing content (optional)
                tbody_ele.innerHTML = "";
			    if(res.message.Daily) {
			        timespan_obj["daily"]["cars_in"] = res.message.Daily.number_of_vehicle_in[0]["total_number_cars_in"]
			        timespan_obj["daily"]["cars_out"] = res.message.Daily.number_of_vehicle_out[0]["total_number_cars_out"];
			        timespan_obj["daily"]["average"] = res.message.Daily.average_time_delivery[0]["average"];

                    // Create new row for Daily
                    let dailyRow = document.createElement("tr");

                    let labelTd = document.createElement("td");
                    labelTd.textContent = "Today";
                    labelTd.style = "border:1px solid #d1d8dd;padding:6px 8px;";
                    dailyRow.appendChild(labelTd);

                    for (const key in timespan_obj.daily) {
                        let td = document.createElement("td");
                        td.textContent = timespan_obj.daily[key];
                        td.style = "border:1px solid #d1d8dd;padding:6px 8px;";
                        dailyRow.appendChild(td);
                    }

                    tbody_ele.appendChild(dailyRow);
			    }
			    if(res.message.Monthly) {
			        timespan_obj["monthly"]["cars_in"] = res.message.Monthly.number_of_vehicle_in[0]["total_number_cars_in"]
			        timespan_obj["monthly"]["cars_out"] = res.message.Monthly.number_of_vehicle_out[0]["total_number_cars_out"]
			        timespan_obj["monthly"]["average"] = res.message.Monthly.average_time_delivery[0]["average"];

                    // Create new row for Monthly
                    let monthlyRow = document.createElement("tr");

                    let labelTd = document.createElement("td");
                    labelTd.textContent = "This Month";
                    labelTd.style = "border:1px solid #d1d8dd;padding:6px 8px;";
                    monthlyRow.appendChild(labelTd);

                    for (const key in timespan_obj.monthly) {
                        let td = document.createElement("td");
                        td.textContent = timespan_obj.monthly[key];
                        td.style = "border:1px solid #d1d8dd;padding:6px 8px;";
                        monthlyRow.appendChild(td);
                    }
                    tbody_ele.appendChild(monthlyRow);
			    }
			}
		}
	});

window.drill_down = function(ro_status, workshop_division, bill_type, customer_type) {

		frappe.route_options = {
			from_date: frappe.defaults.get_user_default("year_start_date"),
			to_date: frappe.defaults.get_user_default("year_end_date"),
			workshop_division: workshop_division,
			project_status: ro_status
		};

		if (bill_type) {
			frappe.route_options["billing_type"] = bill_type;
		}

        frappe.set_route("query-report", "BRAC I_O");
	};


frappe.call({
		method: "automotive.events.vehicle_movement.fetch_ro_project_status_based_workshop_division_for_vehicle",
		freeze: 1,
		freeze_message: __("Fetching.."),
		callback: (res) => {
		    var custom_order = [
        		"To Estimate",
        		"Awaiting Approval",
        		"To Assign",
        		"Assigned",
        		"In Progress",
        		"Awaiting Parts",
        		"Parts Arrived",
        		"Tasks Completed",
        		"Awaiting LPO",
        		"Awaiting Gate Pass",
        		"Ready for Collection",
        		"Not Invoiced",
        		"Total Loss",
        		"Completed"
        	] 
			if(res.message) {
			    let tbody_ele = root_element.querySelector('#vehicle_movement_tbody_division');
			    tbody_ele.innerHTML = "";
			    let tr_message = "";
			    for(let each_project_defined_status of custom_order) {
    			    let found_mechanical = false, found_brac = false, found_bs_cash = false, found_bs_insurance = false;

    			    tr_message += "<tr><td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+each_project_defined_status+"</td>";
    			    for(let each_dict_mechanical of res.message.mechanical_category) {
    			        if(each_dict_mechanical.project_status == each_project_defined_status){
    			            found_mechanical = true;
    			            tr_message += "<td onclick='drill_down(\""+each_project_defined_status+"\", `Mechanical`, null)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>"+each_dict_mechanical.total_ro+"</td>"+"<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+each_dict_mechanical.average+"</td>"
    			        }
    			    }
    			    if(!found_mechanical) {
    			        tr_message += "<td onclick='drill_down(\""+each_project_defined_status+"\", `Mechanical`, null)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>0</td><td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>"
    			    }
    			    for(let each_dict_bs_cash of res.message.body_shop_cash_category) {
    			        if(each_dict_bs_cash.project_status == each_project_defined_status){
    			            found_bs_cash = true;
    			            tr_message += "<td onclick='drill_down(\""+each_project_defined_status+"\", `Body Shop`,`Customer`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>"+each_dict_bs_cash.total_ro+"</td>"+"<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+each_dict_bs_cash.average+"</td>"
    			        }
    			    }
    			    if(!found_bs_cash) {
    			        tr_message += "<td onclick='drill_down(\""+each_project_defined_status+"\", `Body Shop`,`Customer`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>0</td><td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>"
    			    }
    			    for(let each_dict_bs_insurance of res.message.body_shop_insurance_category) {
    			        if(each_dict_bs_insurance.project_status == each_project_defined_status){
    			            found_bs_insurance = true;
    			            tr_message += "<td onclick='drill_down(\""+each_project_defined_status+"\", `Body Shop`,`Insurance`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>"+each_dict_bs_insurance.total_ro+"</td>"+"<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+each_dict_bs_insurance.average+"</td>"
    			        }
    			    }
    			    if(!found_bs_insurance) {
    			        tr_message += "<td onclick='drill_down(\""+each_project_defined_status+"\", `Body Shop`,`Insurance`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>0</td><td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>"
    			    }
    			    for(let each_dict_brac of res.message.brac_category) {
    			        if(each_dict_brac.project_status == each_project_defined_status){
    			            found_brac = true;
    			            tr_message += "<td onclick='drill_down(\""+each_project_defined_status+"\", null, null, `Brac`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>"+each_dict_brac.total_ro+"</td>"+"<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+each_dict_brac.average+"</td>"
    			        }
    			    }
    			    if(!found_brac) {
    			        tr_message += "<td onclick='drill_down(\""+each_project_defined_status+"\", null, null, `Brac`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>0</td><td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>"
    			    }
    			    tr_message += "</tr>"
    			    tbody_ele.innerHTML = tr_message;
			    }
			 //   tbody_ele.append(tr_message);
            }
    }
});
