// Helper to format date as YYYY-MM-DD
function formatDate(date) {
    const yyyy = date.getFullYear();
    const mm = String(date.getMonth() + 1).padStart(2, "0"); // months are 0-based
    const dd = String(date.getDate()).padStart(2, "0");
    return `${yyyy}-${mm}-${dd}`;
}

// Example helper functions
function getFiscalYear(date) {
    const year = date.getFullYear();
    const start_date = new Date(year, 0, 1); // Jan 1
    const end_date = new Date(year, 11, 31); // Dec 31
    return [start_date, end_date];
}

function getFirstDay(date) {
    return new Date(date.getFullYear(), date.getMonth(), 1);
}

window.drill_down_ytd = function(ro_status, workshop_division, bill_type, customer_type, 
timespan, branch, project_task_type_dict) {

		let project_status = ro_status;
		let current_task_type = null;
		for(let each_ro_task_status of project_task_type) {
		    if(each_ro_task_status[ro_status]) {
		        project_status = each_ro_task_status[ro_status];
		        current_task_type = each_ro_task_status["current_task_type"];
		        break;
		    }
		}
		frappe.route_options = {
			workshop_division: workshop_division,
			project_status: project_status
		};

		let from_date, to_date;
        const today_date = new Date();

        if (timespan === "YTD") {
            from_date = formatDate(getFiscalYear(today_date)[0]); // start of fiscal year
            to_date = formatDate(today_date);
        } else if (timespan === "MTD") {
            from_date = formatDate(getFirstDay(today_date));
            to_date = formatDate(today_date);
        }

        frappe.route_options["from_date"] = from_date;
        frappe.route_options["to_date"] = to_date;


		if(current_task_type) {
		    frappe.route_options["current_task_type"] = current_task_type;
		}

		if (bill_type) {
			frappe.route_options["billing_type"] = bill_type;
		}

		if (branch) {
		    frappe.route_options["branch"] = branch;
		}

		frappe.route_options["workspace"] = "Vehicle Mobility BRAC";
		frappe.route_options["report_link_access_roles_vehicle_mobility_field"] = "report_link_access_roles_brac_vehicle_mobility";
		frappe.open_in_new_tab = true;
        frappe.set_route("query-report", "Related Parties I_O");
	};

