function fetch_invoice_wip_data(from_date, to_date, wip_timespan) {

    // tbody_ele_autoworks_invoice_wip_map.innerHTML = "";
    // tbody_ele_autocare_invoice_wip_map.innerHTML = "";

    frappe.call({
    		method: "pitstop_email_digest.utils.vehicle_movement.vehicle_movement_invoice_wip.fetch_revenue_branchwise_based_on_costcenter",
    		args: {
    		  from_date: from_date,
    		  to_date: to_date,
    		  wip_timespan: wip_timespan
    		},
    		freeze: 1,
    		freeze_message: __("Fetching.."),
    		callback: (res) => {
    		    if(res.message && res.message.cost_center_branch_list) {
    		        if(res.message.cost_center_branch_list.length>0) {
    		            let autoworks_branch_list = [];

    		            for(let each_cost_center_branch_list of res.message.cost_center_branch_list) {
    		                if(each_cost_center_branch_list.cost_center == "AutoWorks - PASLLC") {
    		                    for(let row_each_cost_center_branch_list_branch_revenue_details of each_cost_center_branch_list.branch_revenue_details) {
    		                        autoworks_branch_list.push(row_each_cost_center_branch_list_branch_revenue_details.Branch);
    		                    }
    		                }
    		            }

    		            for(let each_customer_group_cost_center_revenue_list of res.message.customer_group_cost_center_revenue_list) {
    		                if(each_customer_group_cost_center_revenue_list.cost_center == "AutoWorks - PASLLC") {
    		                    for(let row_each_customer_group_cost_center_revenue_list of each_customer_group_cost_center_revenue_list.customer_group_cost_center_details) {
    		                        autoworks_branch_list.push(row_each_customer_group_cost_center_revenue_list.branch);
    		                    }
    		                }
    		            }

    		            autoworks_branch_list = [...new Set(autoworks_branch_list)];

    		            //first we process autoworks
    		            let tr_message_autoworks = ""
    		            for(let each_autoworks_branch_list of autoworks_branch_list) {
    		                tr_message_autoworks += "<tr><td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+each_autoworks_branch_list+"</td>";
    		                for(let each_cost_center_branch_list of res.message.cost_center_branch_list) {
        		                if(each_cost_center_branch_list.cost_center == "AutoWorks - PASLLC") {
        		                    let tdAdded_branch_autoworks = false;
        		                    for(let row_each_cost_center_branch_list_branch_revenue_details of each_cost_center_branch_list.branch_revenue_details) {
        		                        if(row_each_cost_center_branch_list_branch_revenue_details.Branch == each_autoworks_branch_list) {
        		                            tr_message_autoworks += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+Math.round(row_each_cost_center_branch_list_branch_revenue_details.Target).toLocaleString('en-AE')+"</td>"
        		                            tr_message_autoworks += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+Math.round(row_each_cost_center_branch_list_branch_revenue_details.Revenue).toLocaleString('en-AE')+"</td>"
        		                            tr_message_autoworks += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+((row_each_cost_center_branch_list_branch_revenue_details.Target>0)?
        		                            ((row_each_cost_center_branch_list_branch_revenue_details.Revenue/
        		                            row_each_cost_center_branch_list_branch_revenue_details.Target)*100.0):0).toFixed(2)+"</td>";
        		                            tdAdded_branch_autoworks = true;
        		                            break;
        		                        }
        		                    }
        		                    if(!tdAdded_branch_autoworks) {
        		                            tr_message_autoworks += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>"
        		                            tr_message_autoworks += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>"
        		                            tr_message_autoworks += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>"
        		                        }
        		                }
        		            }

        		            let autoworks_customer_group = ["Budget Mobility"];
        		            let autoworks_vehicle_group = ["TESLA"];
        		            let autoworks_others_group = ["Others"];

        		            for(let each_customer_group_cost_center_revenue_list of res.message.customer_group_cost_center_revenue_list) {
        		                if(each_customer_group_cost_center_revenue_list.cost_center == "AutoWorks - PASLLC") {
        		                    for(let each_autoworks_customer_group of autoworks_customer_group) {
        		                        let tdAdded_customer_group_autoworks = false;
            		                    for(let row_each_customer_group_cost_center_details of each_customer_group_cost_center_revenue_list.customer_group_cost_center_details) {
            		                        if((row_each_customer_group_cost_center_details.branch == each_autoworks_branch_list) && 
            		                        (row_each_customer_group_cost_center_details.customer_group == each_autoworks_customer_group)) {
            		                            tr_message_autoworks += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+((row_each_customer_group_cost_center_details.ro_count)?row_each_customer_group_cost_center_details.ro_count:0)+"</td>"
            		                            tr_message_autoworks += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+(row_each_customer_group_cost_center_details.revenue?Math.round(row_each_customer_group_cost_center_details.revenue).toLocaleString('en-AE'):0)+"</td>"
            		                            tdAdded_customer_group_autoworks = true; // mark that we added a value
                                                break;
            		                        }
            		                    }
            		                    // If no match found, add zero
                                        if (!tdAdded_customer_group_autoworks) {
                                            tr_message_autoworks += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>";
                                            tr_message_autoworks += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>";
                                        }
        		                    }

        		                    for(let each_autoworks_vehicle_group of autoworks_vehicle_group) {
        		                        let tdAdded_vehicle_group_autoworks = false;
            		                    for(let row_each_customer_group_cost_center_details of each_customer_group_cost_center_revenue_list.customer_group_cost_center_details) {
            		                        if((row_each_customer_group_cost_center_details.branch == each_autoworks_branch_list) && 
            		                        (row_each_customer_group_cost_center_details.vehicle_group == each_autoworks_vehicle_group)) {
            		                            tr_message_autoworks += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+((row_each_customer_group_cost_center_details.ro_count)?row_each_customer_group_cost_center_details.ro_count:0)+"</td>"
            		                            tr_message_autoworks += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+(row_each_customer_group_cost_center_details.revenue?Math.round(row_each_customer_group_cost_center_details.revenue).toLocaleString('en-AE'):0)+"</td>"
            		                            tdAdded_vehicle_group_autoworks = true; // mark that we added a value
                                                break;
            		                        }
            		                    }
            		                    // If no match found, add zero
                                        if (!tdAdded_vehicle_group_autoworks) {
                                            tr_message_autoworks += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>";
                                            tr_message_autoworks += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>";
                                        }
        		                    }

        		                    for(let each_autoworks_others_group of autoworks_others_group) {
        		                        let tdAdded_others_group_autoworks = false;
            		                    for(let row_each_customer_group_cost_center_details of each_customer_group_cost_center_revenue_list.customer_group_cost_center_details) {
            		                        if((row_each_customer_group_cost_center_details.branch == each_autoworks_branch_list) && 
            		                        (row_each_customer_group_cost_center_details.customer_group == each_autoworks_others_group)) {
            		                            tr_message_autoworks += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+((row_each_customer_group_cost_center_details.ro_count)?row_each_customer_group_cost_center_details.ro_count:0)+"</td>"
            		                            tr_message_autoworks += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+(row_each_customer_group_cost_center_details.revenue?Math.round(row_each_customer_group_cost_center_details.revenue).toLocaleString('en-AE'):0)+"</td>"
            		                            tdAdded_others_group_autoworks = true; // mark that we added a value
                                                break;
            		                        }
            		                    }
            		                    // If no match found, add zero
                                        if (!tdAdded_others_group_autoworks) {
                                            tr_message_autoworks += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>";
                                            tr_message_autoworks += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>";
                                        }
        		                    }

        		                    // Total calculation
