function totalrow_calculation(cost_center, data_array) {
    let total_row_obj = {
        "target_total":0, "revenue_total":0, 
        "brac_ro_count_total":0, "brac_revenue_total":0,
        "tesla_ro_count_total":0, "tesla_revenue_total":0,
        "bmw_ro_count_total":0, "bmw_revenue_total":0,
        "geely_ro_count_total":0, "geely_revenue_total":0,
        "others_ro_count_total":0, "others_revenue_total":0,
        "wip_total_ro_count":0, "wip_total_revenue":0
    };
    if(data_array) {
       for(let each_data_array of data_array.cost_center_branch_list) {
           if(each_data_array.cost_center == cost_center) {
               for(let each_branch_revenue_details of each_data_array.branch_revenue_details) {
                   total_row_obj.target_total += each_branch_revenue_details.Target;
                   total_row_obj.revenue_total += each_branch_revenue_details.Revenue;
               }
           }
       }

       for(let each_customer_group_cost_center_revenue_list of data_array.customer_group_cost_center_revenue_list) {
           if(each_customer_group_cost_center_revenue_list.cost_center == cost_center) {
               for(let each_customer_group_cost_center_details of each_customer_group_cost_center_revenue_list.customer_group_cost_center_details) {
                   if(each_customer_group_cost_center_details.customer_group == "Budget Mobility") {
                        total_row_obj.brac_ro_count_total += each_customer_group_cost_center_details.ro_count;
                        total_row_obj.brac_revenue_total += each_customer_group_cost_center_details.revenue;   
                    }
                    if(each_customer_group_cost_center_details.vehicle_group == "TESLA") {
                        total_row_obj.tesla_ro_count_total += each_customer_group_cost_center_details.ro_count;
                        total_row_obj.tesla_revenue_total += each_customer_group_cost_center_details.revenue;   
                    }
                    if(each_customer_group_cost_center_details.vehicle_group == "BMW") {
                        total_row_obj.bmw_ro_count_total += each_customer_group_cost_center_details.ro_count;
                        total_row_obj.bmw_revenue_total += each_customer_group_cost_center_details.revenue;   
                    }
                    if(each_customer_group_cost_center_details.vehicle_group == "GEELY") {
                        total_row_obj.geely_ro_count_total += each_customer_group_cost_center_details.ro_count;
                        total_row_obj.geely_revenue_total += each_customer_group_cost_center_details.revenue;   
                    }
                    if(each_customer_group_cost_center_details.customer_group == "Others") {
                        total_row_obj.others_ro_count_total += each_customer_group_cost_center_details.ro_count;
                        total_row_obj.others_revenue_total += each_customer_group_cost_center_details.revenue;   
                    }
                    total_row_obj.wip_total_ro_count += each_customer_group_cost_center_details.ro_count;
                    total_row_obj.wip_total_revenue += each_customer_group_cost_center_details.revenue;
                }
           }
       }

       return total_row_obj
    }
}

