wip_timespan_control.df.onchange = function() {
    let selected_wip_timespan = wip_timespan_control.get_value();

    if(!from_date_wrapper_control.get_value()) {
        set_no_data_method();
        frappe.throw("From date is mandatory");

    }
    if(!to_date_wrapper_control.get_value()) {
        set_no_data_method();
        frappe.throw("To date is mandatory");
    }

    if(to_date_wrapper_control.get_value()<from_date_wrapper_control.get_value()) {
        set_no_data_method();
        frappe.throw("From Date should before the To date");
    }
    let from_date_value = from_date_wrapper_control.get_value();
    let from_date = new Date(from_date_value);

    // check if it's not first day
    if (from_date.getDate() !== 1) {
        set_no_data_method();
        frappe.throw("From Date must be the first day of the month");
    }
    wip_timespan_header();
    fetch_invoice_wip_data(from_date_wrapper_control.get_value(), to_date_wrapper_control.get_value(), selected_wip_timespan);
};

apply_date_filter_btn.addEventListener("click", () => {
    if(!from_date_wrapper_control.get_value()) {
