    if (from_date.getDate() !== 1) {
        set_no_data_method();
        frappe.throw("From Date must be the first day of the month");
    }
    wip_timespan_header();
    fetch_invoice_wip_data(from_date_wrapper_control.get_value(), to_date_wrapper_control.get_value(), timespan_wip);

});

