script:
var timespan_obj = {"daily":{}, "monthly":{}};
frappe.call({
		method: "pitstop_email_digest.utils.vehicle_movement.vehicle_movement.get_vehicle_movement",
		args: {
		    customer_list:["C00883", "C00276", "C01053"]
		},
		freeze: 1,
		freeze_message: __("Fetching.."),
		callback: (res) => {
			if(res.message) {
			    console.log(res.message);
			    let tbody_ele = root_element.querySelector('#vehicle_movement_tbody');
                // Clear any existing content (optional)
                tbody_ele.innerHTML = "";
			    if(res.message.Daily) {
			        timespan_obj["daily"]["cars_in"] = res.message.Daily.number_of_vehicle_in[0]["total_number_cars_in"]
			        timespan_obj["daily"]["cars_out"] = res.message.Daily.number_of_vehicle_out[0]["total_number_cars_out"];
			        timespan_obj["daily"]["average"] = res.message.Daily.average_time_delivery[0]["average"];

                    // Create new row for Daily
                    let dailyRow = document.createElement("tr");

                    let labelTd = document.createElement("td");
                    labelTd.textContent = "Today";
                    labelTd.style = "border:1px solid #d1d8dd;padding:6px 8px;";
                    dailyRow.appendChild(labelTd);

                    for (const key in timespan_obj.daily) {
                        let td = document.createElement("td");
                        td.textContent = timespan_obj.daily[key];
                        td.style = "border:1px solid #d1d8dd;padding:6px 8px;";
                        dailyRow.appendChild(td);
                    }

                    tbody_ele.appendChild(dailyRow);
			    }
			    if(res.message.Monthly) {
			        timespan_obj["monthly"]["cars_in"] = res.message.Monthly.number_of_vehicle_in[0]["total_number_cars_in"]
			        timespan_obj["monthly"]["cars_out"] = res.message.Monthly.number_of_vehicle_out[0]["total_number_cars_out"]
			        timespan_obj["monthly"]["average"] = res.message.Monthly.average_time_delivery[0]["average"];

                    // Create new row for Monthly
                    let monthlyRow = document.createElement("tr");

                    let labelTd = document.createElement("td");
                    labelTd.textContent = "This Month";
                    labelTd.style = "border:1px solid #d1d8dd;padding:6px 8px;";
                    monthlyRow.appendChild(labelTd);

                    for (const key in timespan_obj.monthly) {
                        let td = document.createElement("td");
                        td.textContent = timespan_obj.monthly[key];
                        td.style = "border:1px solid #d1d8dd;padding:6px 8px;";
                        monthlyRow.appendChild(td);
                    }
                    tbody_ele.appendChild(monthlyRow);
			    }
			}
		}
	});

let some_class_element = root_element.querySelector('#vehicle_movement_table');

if (some_class_element) {
	some_class_element.style.display = 'none';
}

window.drill_down_ytd = function(ro_status, workshop_division, bill_type, customer_type, timespan) {

		frappe.route_options = {
			from_date: frappe.defaults.get_user_default("year_start_date"),
			to_date: frappe.datetime.get_today(),
			workshop_division: workshop_division,
			project_status: ro_status
		};

		if(timespan == "YTD") {
		    frappe.route_options["from_date"] = frappe.defaults.get_user_default("year_start_date");
		}
		else if(timespan == "MTD") {
		    frappe.route_options["from_date"] = frappe.datetime.month_start();
		}

		if (bill_type) {
			frappe.route_options["billing_type"] = bill_type;
		}

        frappe.set_route("query-report", "Related Parties I_O");
	};

const division_dict = [
		{"workshop_division": "Mechanical", "bill_to_customer_check": null, "customer_list":["C00883", "C00276", "C01053"], "category": "Mechanical"},
		{"workshop_division": null, "bill_to_customer_check": null, "customer_list":["C00883", "C00276", "C01053"], "category": "BRAC"},
		{"workshop_division": "Body Shop", "bill_to_customer_check": "Same", "customer_list":["C00883", "C00276", "C01053"], "category": "Body Shop - Cash"},
		{"workshop_division": "Body Shop", "bill_to_customer_check": "Insurance", "customer_list":["C00883", "C00276", "C01053"],  "category": "Body Shop - Insurance"},
	]


frappe.call({
		method: "pitstop_email_digest.utils.vehicle_movement.vehicle_movement.fetch_ro_project_status_based_workshop_division_for_vehicle",
		args: {
		  division_dict: division_dict,
		  timespan: "MTD and YTD"
		},
		freeze: 1,
		freeze_message: __("Fetching.."),
		callback: (res) => {
		    var custom_order = [
        		"To Estimate",
        		"Awaiting Approval",
        		"To Assign",
        		"Assigned",
        		"In Progress",
        		"Awaiting Parts",
        		"Parts Arrived",
        		"Tasks Completed",
        		"Awaiting LPO",
        		"Awaiting Gate Pass",
        		"Ready for Collection",
        		"Not Invoiced",
        		"Total Loss"
        	] 
			if(res.message) {
			    console.log(res.message);
			    let tbody_ele = root_element.querySelector('#vehicle_movement_tbody_division');
			    tbody_ele.innerHTML = "";
			    let tr_message = "";
			    for(let each_project_defined_status of custom_order) {
    			    let found_mechanical_mtd = false, found_mechanical_ytd = false, found_brac_mtd = false, found_brac_ytd = false, 
    			    found_bs_cash_mtd = false, found_bs_cash_ytd = false, found_bs_insurance_mtd = false, found_bs_insurance_ytd = false;

    			    tr_message += "<tr><td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+each_project_defined_status+"</td>";
    			    for(let each_dict_mechanical of res.message.mechanical_category) {
    			        if((each_dict_mechanical.project_status == each_project_defined_status) && (each_dict_mechanical.MTD)){
    			            found_mechanical_mtd = true;
    			            tr_message += "<td onclick='drill_down_ytd(\""+each_project_defined_status+"\", `Mechanical`, null, null, `MTD`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>"+each_dict_mechanical.total_ro+"</td>"+"<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+each_dict_mechanical.average+"</td>"
    			        }
    			        if((each_dict_mechanical.project_status == each_project_defined_status) && (each_dict_mechanical.YTD)){
    			            found_mechanical_ytd = true;
    			            tr_message += "<td onclick='drill_down_ytd(\""+each_project_defined_status+"\", `Mechanical`, null, null, `YTD`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>"+each_dict_mechanical.total_ro+"</td>"+"<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+each_dict_mechanical.average+"</td>"
    			        }
    			    }
    			    if(!found_mechanical_mtd) {
    			        tr_message += "<td onclick='drill_down_ytd(\""+each_project_defined_status+"\", `Mechanical`, null, null, `MTD`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>0</td><td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>"
    			    }
    			    if(!found_mechanical_ytd) {
    			        tr_message += "<td onclick='drill_down_ytd(\""+each_project_defined_status+"\", `Mechanical`, null, null, `YTD`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>0</td><td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>"
    			    }
    			    for(let each_dict_bs_cash of res.message.body_shop_cash_category) {
    			        if((each_dict_bs_cash.project_status == each_project_defined_status) && (each_dict_bs_cash.MTD)){
    			            found_bs_cash_mtd = true;
    			            tr_message += "<td onclick='drill_down_ytd(\""+each_project_defined_status+"\", `Body Shop`,`Customer`, null, `MTD`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>"+each_dict_bs_cash.total_ro+"</td>"+"<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+each_dict_bs_cash.average+"</td>"
    			        }
    			        if((each_dict_bs_cash.project_status == each_project_defined_status) && (each_dict_bs_cash.YTD)){
    			            found_bs_cash_ytd = true;
    			            tr_message += "<td onclick='drill_down_ytd(\""+each_project_defined_status+"\", `Body Shop`,`Customer`, null, `YTD`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>"+each_dict_bs_cash.total_ro+"</td>"+"<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+each_dict_bs_cash.average+"</td>"
    			        }
    			    }
    			    if(!found_bs_cash_mtd) {
    			        tr_message += "<td onclick='drill_down_ytd(\""+each_project_defined_status+"\", `Body Shop`,`Customer`, null, `MTD`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>0</td><td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>"
    			    }
    			    if(!found_bs_cash_ytd) {
    			        tr_message += "<td onclick='drill_down_ytd(\""+each_project_defined_status+"\", `Body Shop`,`Customer`, null, `YTD`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>0</td><td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>"
    			    }
    			    for(let each_dict_bs_insurance of res.message.body_shop_insurance_category) {
    			        if((each_dict_bs_insurance.project_status == each_project_defined_status) && (each_dict_bs_insurance.MTD)){
    			            found_bs_insurance_mtd = true;
    			            tr_message += "<td onclick='drill_down_ytd(\""+each_project_defined_status+"\", `Body Shop`,`Insurance`, null, `MTD`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>"+each_dict_bs_insurance.total_ro+"</td>"+"<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+each_dict_bs_insurance.average+"</td>"
    			        }
    			        if((each_dict_bs_insurance.project_status == each_project_defined_status) && (each_dict_bs_insurance.YTD)){
    			            found_bs_insurance_ytd = true;
    			            tr_message += "<td onclick='drill_down_ytd(\""+each_project_defined_status+"\", `Body Shop`,`Insurance`, null, `YTD`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>"+each_dict_bs_insurance.total_ro+"</td>"+"<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+each_dict_bs_insurance.average+"</td>"
    			        }
    			    }
    			    if(!found_bs_insurance_mtd) {
    			        tr_message += "<td onclick='drill_down_ytd(\""+each_project_defined_status+"\", `Body Shop`,`Insurance`, null,  `MTD`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>0</td><td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>"
    			    }
    			    if(!found_bs_insurance_ytd) {
    			        tr_message += "<td onclick='drill_down_ytd(\""+each_project_defined_status+"\", `Body Shop`,`Insurance`, null, `YTD`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>0</td><td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>"
    			    }
    			    for(let each_dict_brac of res.message.brac_category) {
    			        if((each_dict_brac.project_status == each_project_defined_status) && (each_dict_brac.MTD)){
    			            found_brac_mtd = true;
    			            tr_message += "<td onclick='drill_down_ytd(\""+each_project_defined_status+"\", null, null, `Brac`, `MTD`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>"+each_dict_brac.total_ro+"</td>"+"<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+each_dict_brac.average+"</td>"
    			        }
    			        if((each_dict_brac.project_status == each_project_defined_status) && (each_dict_brac.YTD)){
    			            found_brac_ytd = true;
    			            tr_message += "<td onclick='drill_down_ytd(\""+each_project_defined_status+"\", null, null, `Brac`, `YTD`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>"+each_dict_brac.total_ro+"</td>"+"<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+each_dict_brac.average+"</td>"
    			        }
    			    }
    			    if(!found_brac_mtd) {
    			        tr_message += "<td onclick='drill_down_ytd(\""+each_project_defined_status+"\", null, null, `Brac`, `MTD`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>0</td><td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>"
    			    }
    			    if(!found_brac_ytd) {
    			        tr_message += "<td onclick='drill_down_ytd(\""+each_project_defined_status+"\", null, null, `Brac`, `YTD`)' style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;text-decoration: underline;'>0</td><td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>"
    			    }
    			    tr_message += "</tr>"
    			    tbody_ele.innerHTML = tr_message;
    			    some_class_element.style.display = 'block';
			    }
            }
    }
});
