// 5. Define your data-fetching function
function fetchVehicleMovementData(selected_date) {
  const division_dict = [
    {
      "workshop_division": "Body Shop",
      "bill_to_customer_check": null,
      "customer_list": ["C00883", "C00276", "C01053"],
      "category": "BRAC BODYSHOP"
    },
    {
      "workshop_division": "Mechanical",
      "bill_to_customer_check": null,
      "customer_list": ["C00883", "C00276", "C01053"],
      "category": "BRAC MECHANICAL"
    },
  ];

  frappe.call({
    method: "pitstop_email_digest.utils.vehicle_movement.vehicle_movement.fetch_ro_project_status_based_workshop_division_for_vehicle",
    args: {
      division_dict: division_dict,
      timespan: "Custom Date",
      selected_date: selected_date
    },
    freeze: 1,
    freeze_message: __("Fetching.."),
    callback: (res) => {
      var custom_order = [
        		"To Estimate",
        		"Awaiting Approval",
        		"Awaiting Claim No",
        		"To Assign",
        		"Assigned",
        		"In Progress",
        		"Awaiting Parts",
        		"Parts Arrived",
        		"Tasks Completed",
        		"Awaiting LPO",
        		"Awaiting Gate Pass",
        		"Ready for Collection",
        		"Not Invoiced",
        		"Total Loss",
        		"Completed"
        	];
      let tbody_ele = root_element.querySelector('#vehicle_movement_tbody_division_brac_a_day');
      tbody_ele.innerHTML = "";
      let tr_message = "";
      let mechanical_total_count = 0;
      let bodyshop_total_count = 0;
      let brac_total_count = 0;
      if (res.message) {
          for(let each_project_defined_status of custom_order) {
            let status_count_mechanical = 0;
            let status_count_bodyshop = 0;
            let found_mechanical = false;
            let found_bodyshop = false;
            let status_data_tr = "";
            status_data_tr = "<tr><td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+each_project_defined_status+"</td>";
            for(let each_dict_brac_mechanical of res.message.brac_mechanical) {
		        if(each_dict_brac_mechanical.project_status == each_project_defined_status){
		            found_mechanical = true;
		            status_count_mechanical += each_dict_brac_mechanical.total_ro;
		            status_data_tr += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+each_dict_brac_mechanical.total_ro+"</td>";
		        }
		    }
		    if(!found_mechanical){
		           status_data_tr += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>";
		    }
		    for(let each_dict_brac_bodyshop of res.message.brac_bodyshop) {
		        if(each_dict_brac_bodyshop.project_status == each_project_defined_status){
		            found_bodyshop = true;
		            status_count_bodyshop += each_dict_brac_bodyshop.total_ro;
		            status_data_tr += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+each_dict_brac_bodyshop.total_ro+"</td>";
		        }
		    }

		    if(!found_bodyshop){
		           status_data_tr += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>0</td>";
		    }

		    if(found_mechanical || found_bodyshop) {
		        tr_message += status_data_tr;
		        mechanical_total_count += status_count_mechanical;
		        bodyshop_total_count += status_count_bodyshop;
		        let sum_bodyshop_mechanical = status_count_mechanical+status_count_bodyshop;
		        tr_message += "<td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>"+sum_bodyshop_mechanical+"</td></tr>";
		    }
          }
        if(!tr_message) {
            tr_message += `
                <tr>
                    <td colspan="4" style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'>No Data</td>
                </tr>
            `;
        }
        else {
            tr_message += `<tr style='background: #f8f8f8;'>
                  <td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'><b>Total</b></td>
                  <td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'><b>${mechanical_total_count}</b></td>
                  <td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'><b>${bodyshop_total_count}</b></td>
                  <td style='border:1px solid #d1d8dd;padding:6px 8px;text-align:center;'><b>${mechanical_total_count+bodyshop_total_count}</b></td>
                </tr>`;
        }
        tbody_ele.innerHTML = tr_message;
      }
    }
  });
}

